function prepareText(text) {
    let copyText = text;

    copyText = copyText.toLowerCase();
    copyText = copyText.replaceAll('á', 'a');
    copyText = copyText.replaceAll('é', 'e');
    copyText = copyText.replaceAll('í', 'i');
    copyText = copyText.replaceAll('ó', 'o');
    copyText = copyText.replaceAll('ú', 'u');
    copyText = copyText.replaceAll('ñ', 'n-');

    copyText = copyText.replaceAll(/[.,\?\¿\/#!¡$%\^&\*;:{}=\-_`~()]/g, "");
    copyText = copyText.replaceAll(/\s{2,}/g, " ");

    copyText = copyText.trim();

    return copyText;

}

function splitWordsInValidSigns(text) {
    let imagesPath = [];
    let imagesLetter = [];
    let imagesNumbers = arrayImages.numeros;

    const words = text.split(' ');
    
    for (let iWord = 0; iWord < words.length; iWord++) {
        const word = words[iWord];
        imagesLetter = arrayImages[word[0]];

        if(imagesNumbers.indexOf(word + ".jpg") != -1) {
            imagesPath.push("numeros" + '/' + imagesNumbers[imagesNumbers.indexOf(word + '.jpg')]);
        } else if (imagesLetter.indexOf(word + '.jpg') != -1) {
            imagesPath.push(word[0] + '/' + imagesLetter[imagesLetter.indexOf(word + '.jpg')]);
        } else {
            alfabeto = arrayImages['alfabeto'];
            
            for (let iLetter = 0; iLetter < word.length; iLetter++) {
                imagesPath.push('alfabeto/' + alfabeto[alfabeto.indexOf(word[iLetter] + '.jpg')]);
            }
        }
    }
    return imagesPath;
}



function loadImage(signsName) {
    let base = "http://localhost/froacAA/asset/img/lengua/";

    for (let index = 0; index < signsName.length; index++) {
        const element = signsName[index];
        console.log(base + element);

        if ((index + 1) < signsName.length) {
            clips[index] = {
                src: base + element,
                frames: 13,
                cols: 1,
                loops: 1,
                fps: parseInt(localStorage['sign_speed']),
                onEnd: function () {
                    console.log('clip1 ended.');
                    canvidControl.play(index + 1);
                }
            };
        } else {
            clips[index] = {
                src: base + element,
                frames: 13,
                cols: 1,
                loops: 1,
                fps: parseInt(localStorage['sign_speed']),
                onEnd: function () {
                    canvidControl.destroy();
                    $('#stop-iris').hide();
                    $('#play-iris').hide();
                    $('#pause-iris').hide();
                }
            };
        }
    }

    canvidControl = canvid({
        selector: '.video',
        videos: clips,
        width: 320,
        height: 240,
        loaded: function () {
            canvidControl.play(0);
            $('#play-iris').hide();
            $('#pause-iris').show();
            $('#stop-iris').show();

            // reverse playback
            // canvidControl.play('clip1', true);
        }
    });

}

